from typing import List, Dict, Any, Optional
import common

class Client:
    def __init__(self, host: str = common.DEFAULT_HOST, port: int = common.DEFAULT_PORT):
        self.host = host
        self.port = port
        self.pending_commands = []
        self.pending_commands_lock = threading.Lock()
        self.socket = None
        self.client_id = None
        self.current_custom_attributes = {}
        self.clients_attributes = {}
        self.rooms_attributes = {}
        self.current_room = None

    def __del__(self):
        self.disconnect()

    def connect(self) -> None:
        try:
            self.socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
            self.socket.connect((self.host, self.port))
            self.send_command(common.Command('JOIN', {}))
        except (socket.error, ConnectionRefusedError):
            self.handle_connection_lost()

    def disconnect(self) -> None:
        if self.socket:
            try:
                self.socket.shutdown(socket.SHUT_RDWR)
                self.socket.close()
            except socket.error:
                pass
            finally:
                self.socket = None
        self.handle_connection_lost()

    def is_connected(self) -> bool:
        return self.socket is not None

    def send_command(self, command: common.Command) -> bool:
        if not self.is_connected():
            return False
        with self.pending_commands_lock:
            self.pending_commands.append(command)
        return True

    def handle_connection_lost(self) -> None:
        self.client_id = None
        self.current_room = None
        self.current_custom_attributes.clear()
        self.clients_attributes.clear()
        self.rooms_attributes.clear()
        with self.pending_commands_lock:
            self.pending_commands.clear()

    def fetch_incoming_commands(self) -> List[common.Command]:
        commands = []
        if not self.socket:
            return commands
        try:
            data = self.socket.recv(4096)
            if not data:
                self.handle_connection_lost()
                return commands
            lines = data.decode('utf-8').splitlines()
            for line in lines:
                if not line.strip():
                    continue
                cmd_data = json.loads(line)
                command = common.Command.from_json(cmd_data)
                commands.append(command)
                self._process_command(command)
        except socket.error:
            self.handle_connection_lost()
        return commands

    def _process_command(self, command: common.Command) -> None:
        if command.type == 'SET_CLIENT_ID':
            self.client_id = command.data.get('client_id')
        elif command.type == 'JOIN_ROOM':
            self.current_room = command.data.get('room_name')
        elif command.type == 'LEAVE_ROOM':
            if self.current_room == command.data.get('room_name'):
                self.current_room = None
        elif command.type == 'UPDATE_ATTRIBUTES':
            target = command.data.get('target')
            attrs = command.data.get('attributes', {})
            if target == 'self':
                self.current_custom_attributes.update(attrs)
            elif target.startswith('client_'):
                client_id = target.split('_', 1)[1]
                if client_id not in self.clients_attributes:
                    self.clients_attributes[client_id] = {}
                self.clients_attributes[client_id].update(attrs)
            elif target.startswith('room_'):
                room_id = target.split('_', 1)[1]
                if room_id not in self.rooms_attributes:
                    self.rooms_attributes[room_id] = {}
                self.rooms_attributes[room_id].update(attrs)

    def fetch_outgoing_commands(self, commands_send_interval: int = 0) -> None:
        if not self.socket:
            return
        with self.pending_commands_lock:
            commands = list(self.pending_commands)
            self.pending_commands.clear()
        for cmd in commands:
            try:
                payload = json.dumps(cmd.to_dict()) + '\n'
                self.socket.sendall(payload.encode('utf-8'))
            except socket.error:
                self.handle_connection_lost()
                break
        if commands_send_interval > 0:
            time.sleep(commands_send_interval)

    def fetch_commands(self, commands_send_interval: int = 0) -> List[common.Command]:
        self.fetch_outgoing_commands(commands_send_interval)
        return self.fetch_incoming_commands()

