from typing import Any, Optional, Union, List, Dict, Set

class CSocket:
    def __init__(self, sock: str, timeout: int = -1) -> None:
        self.__csock = socket.socket(socket.AF_UNIX, socket.SOCK_STREAM)
        self.__csock.connect(sock)
        self.__deftout = float(timeout) if timeout != -1 else None
        if self.__deftout is not None:
            self.__csock.settimeout(self.__deftout)

    def __del__(self) -> None:
        self.close()

    def send(self, msg: Any, nonblocking: bool = False, timeout: Optional[float] = None) -> Any:
        original_timeout = self.__csock.gettimeout()
        try:
            if timeout is not None:
                self.__csock.settimeout(timeout)
            elif nonblocking:
                self.__csock.settimeout(0.0)
            else:
                self.__csock.settimeout(self.__deftout)
            data = self.convert(msg)
            serialized = pickle.dumps(data)
            length = len(serialized)
            self.__csock.sendall(struct.pack('!I', length))
            self.__csock.sendall(serialized)
            return self.receive(self.__csock, nonblocking, timeout)
        finally:
            self.__csock.settimeout(original_timeout)

    def settimeout(self, timeout: float) -> None:
        if timeout == -1:
            self.__csock.settimeout(self.__deftout)
        else:
            self.__csock.settimeout(timeout)

    def close(self) -> None:
        try:
            self.__csock.shutdown(socket.SHUT_RDWR)
        except:
            pass
        finally:
            self.__csock.close()

    @staticmethod
    def convert(m: Any) -> Union[str, bool, int, float, List, Dict, Set]:
        if isinstance(m, (str, bool, int, float)):
            return m
        if isinstance(m, list):
            return [CSocket.convert(item) for item in m]
        if isinstance(m, dict):
            return {k: CSocket.convert(v) for k, v in m.items()}
        if isinstance(m, set):
            return set(CSocket.convert(list(m)))
        if hasattr(m, '__dict__'):
            return CSocket.convert(m.__dict__)
        try:
            return float(m)
        except:
            try:
                return str(m)
            except:
                return None

    @staticmethod
    def receive(sock: socket.socket, nonblocking: bool = False, timeout: Optional[float] = None) -> Any:
        old_timeout = sock.gettimeout()
        try:
            if timeout is not None:
                sock.settimeout(timeout)
            elif nonblocking:
                sock.settimeout(0.0)
            raw_len = sock.recv(4)
            if not raw_len:
                return None
            msg_len = struct.unpack('!I', raw_len)[0]
            data = b''
            while len(data) < msg_len:
                remaining = msg_len - len(data)
                chunk = sock.recv(remaining)
                if not chunk:
                    return None
                data += chunk
            return pickle.loads(data)
        except socket.timeout:
            return None
        finally:
            sock.settimeout(old_timeout)

