class XDCCRequest:
    def __init__(self, proxies: Dict[str, Any], bucket: Any):
        self.proxies = proxies
        self.bucket = bucket
        self.fh = None
        self.dccsock = None
        self.filesize = 0
        self.received = 0
        self.speeds = []
        self.sleep = 0.0
        self.last_recv_size = 0
        self.send_64bits_ack = False
        self.abort = False
        self.status_notify = None

    def create_socket(self) -> socket.socket:
        return socket.socket(socket.AF_INET, socket.SOCK_STREAM)

    def _write_func(self, buf: bytes) -> None:
        size = len(buf)
        while not self.abort:
            if self.bucket.consume(size):
                break
            time.sleep(self.sleep)
        self.fh.write(buf)
        self.received += size
        self.last_recv_size = size
        self.sleep = max(0.001, self.sleep * 0.9)

    def _send_ack(self) -> None:
        if self.send_64bits_ack:
            ack_data = struct.pack('!Q', self.received)
        else:
            ack_data = struct.pack('!I', self.received)
        self.dccsock.sendall(ack_data)

    def download(self, ip: str, port: int, filename: str, status_notify: Optional[Callable] = None, resume: Optional[Callable] = None) -> str:
        self.abort = False
        self.status_notify = status_notify
        self.filesize = 0
        self.received = 0
        self.dccsock = self.create_socket()
        try:
            self.dccsock.connect((ip, port))
            if resume:
                resume(self)
            if not self.fh:
                self.fh = open(filename, 'wb')
            else:
                filename = self.fh.name
            while not self.abort:
                data = self.dccsock.recv(4096)
                if not data:
                    break
                start_time = time.time()
                self._write_func(data)
                self._send_ack()
                elapsed = time.time() - start_time
                if elapsed > 0:
                    speed = len(data) / elapsed
                    self.speeds.append(speed)
                    if len(self.speeds) > 3:
                        self.speeds.pop(0)
                self.update_progress()
            self.close()
            return filename
        except Exception as e:
            self.close()
            raise
        finally:
            self.abort = False

    def abort_downloads(self) -> None:
        self.abort = True

    def update_progress(self) -> None:
        if self.status_notify:
            self.status_notify(self)

    def close(self) -> None:
        if self.dccsock:
            self.dccsock.close()
            self.dccsock = None
        if self.fh:
            self.fh.close()
            self.fh = None

    @property
    def size(self) -> int:
        return self.filesize

    @property
    def arrived(self) -> int:
        return self.received

    @property
    def speed(self) -> int:
        if not self.speeds:
            return 0
        return int(sum(self.speeds) / len(self.speeds))

    @property
    def percent(self) -> int:
        if self.filesize == 0:
            return 0
        return int((self.received / self.filesize) * 100)

