from typing import Optional, Tuple, Dict, Any

class SocketLibrary:
    def create_tcp_socket(self, host: str, port: int, timeout: float) -> Optional[Tuple[socket.socket, bool]]:
        context = ssl.create_default_context()
        sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        sock.settimeout(timeout)
        try:
            sock.connect((host, port))
        except socket.error:
            sock.close()
            return None
        try:
            sslsock = context.wrap_socket(sock, server_hostname=host)
            return (sslsock, True)
        except (ssl.SSLError, ssl.CertificateError):
            sock.close()
        sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        sock.settimeout(timeout)
        try:
            sock.connect((host, port))
            return (sock, False)
        except socket.error:
            sock.close()
            return None

    def tcp_connect_only(self, host: str, port: int, timeout: float) -> Optional[Dict[str, Any]]:
        sock_info = self.create_tcp_socket(host, port, timeout)
        if not sock_info:
            return None
        sock, ssl_flag = sock_info
        try:
            peer = sock.getpeername()
        except socket.error:
            sock.close()
            return None
        try:
            service = socket.getservbyport(port, 'tcp')
        except OSError:
            service = None
        result = {
            'peer': peer,
            'service': service,
            'ssl': ssl_flag
        }
        sock.close()
        return result

    def tcp_connect_send_and_receive(self, host: str, port: int, timeout: float) -> Optional[Dict[str, Any]]:
        sock_info = self.create_tcp_socket(host, port, timeout)
