import socket
import struct

class RConConnection:
    def __init__(self, target: str, port: int) -> None:
        self.sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.sock.connect((target, port))
        self.rid = 0

    def send(self, t: int, payload: str) -> bytes:
        current_rid = self.rid
        self.rid += 1
        body_part = payload.encode('utf-8') + b'\x00\x00'
        full_packet = struct.pack('<ii', current_rid, t) + body_part
        size = len(full_packet)
        size_bytes = struct.pack('<i', size)
        self.sock.sendall(size_bytes + full_packet)
        resp_size_bytes = self.sock.recv(4)
        if not resp_size_bytes:
            raise ConnectionError("Connection closed while reading response size")
        resp_size = struct.unpack('<i', resp_size_bytes)[0]
        remaining = resp_size
        resp_data = b''
        while remaining > 0:
            chunk = self.sock.recv(remaining)
            if not chunk:
                raise ConnectionError("Connection reset while reading response")
            resp_data += chunk
            remaining -= len(chunk)
        if len(resp_data) != resp_size:
            raise ConnectionError("Incomplete response data received")
        resp_rid, resp_type = struct.unpack('<ii', resp_data[:8])
        if resp_rid != current_rid:
            raise ValueError(f"Response RID mismatch: expected {current_rid}, got {resp_rid}")
        body = resp_data[8:]
        parts = body.split(b'\x00', 1)
        if len(parts) >= 1:
            return parts[0]
        else:
            return b''

    def login(self, password: str) -> None:
        response = self.send(3, password)
        if not response:
            raise PermissionError("Authentication failed")

    def command(self, com: str, args: str) -> None:
        self.send(2, f"{com} {args}")

    def close(self) -> None:
        self.sock.close()

