            try:
                r, w, e = select.select([self.sock], [self.sock], [self.sock], 0.1)
                if e:
                    self.logger.error("Socket exception")
                    self.connected = False
                if r:
                    data = self.sock.recv(4096)
                    if not data:
                        self.logger.info("Server disconnected")
                        self.connected = False
