    def recvall(self, size: int) -> bytearray:
        data = bytearray()
        while len(data) < size:
            chunk = self.recv(size - len(data))
            if not chunk:
                raise ConnectionError("Connection closed before receiving all data")
            data.extend(chunk)
        return data

    def sendall(self, data: Union[bytes, bytearray]) -> int:
        with self._lock:
            self._sock.sendall(data)
            return len(data)

    def switch_to_ssl(self, pemfile: str):
        context = ssl.create_default_context(ssl.Purpose.SERVER_AUTH)
        context.load_cert_chain(certfile=pemfile)
        self._dup_sock = self._sock.dup()
        try:
            ssl_sock = context.wrap_socket(self._sock, server_hostname='localhost')
            self._sock = ssl_sock
            self._dup_sock.close()
            self._dup_sock = None
        except Exception as e:
            self._sock = self._dup_sock
            self._dup_sock = None
            raise RuntimeError(f"SSL upgrade failed: {e}")

    def __enter__(self):
        return self

    def __exit__(self, *args):
        self.close()

