        server_socket.settimeout(self.DEFAULT_TIMEOUT)
        server_socket.bind(('localhost', self.port))
        server_socket.listen(1)
        conn, addr = server_socket.accept()
        conn.settimeout(self.DEFAULT_TIMEOUT)
        server_socket.close()
