        json_str = json.dumps(dictionary)
        self._send_string(json_str)

    def _get_json_dict(self) -> Dict[str, Any]:
        json_str = self._get_data()
        return json.loads(json_str) if json_str is not None else {}

    def _get_data(self) -> Optional[str]:
        length_bytes = b''
        while len(length_bytes) < 4:
            chunk = self.connection.recv(4 - len(length_bytes))
            if not chunk:
                return None
            length_bytes += chunk
        length = struct.unpack('!I', length_bytes)[0]
        data = b''
        while len(data) < length:
            chunk = self.connection.recv(length - len(data))
            if not chunk:
                return None
            data += chunk
        return data.decode('utf-8')

    def _send_string(self, string: str) -> None:
        data = string.encode('utf-8')
        length = len(data)
        self.connection.sendall(struct.pack('!I', length) + data)

    def close(self) -> None:
        self.connection.close()

