                    threading.Thread(target=receive_data, args=(data_socket, True)).start()
                else:
                    receive_data(data_socket, False)
            else:
                send_data(data_socket, (protocol == UDP), duration)
            elapsed = time.time() - start_time
            client_socket.sendall(struct.pack('!I', TEST_END))
            cmd = struct.unpack('!I', client_socket.recv(4))[0]
            if cmd != EXCHANGE_RESULTS:
                raise ValueError("Expected EXCHANGE_RESULTS command")
            stats_data = struct.pack('!dQ', elapsed, total_bytes)
            client_socket.sendall(stats_data)
