class TCPForwardServer:
    def __init__(self, lhost: str, lport: int, rdev: str, rport: int) -> None:
        self._server = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self._server.setblocking(False)
        self._server.bind((lhost, lport))
        self._server.listen(5)
        self._server.setblocking(False)
        self.input_list: List[socket.socket] = [self._server]
        self.channel: Dict[socket.socket, socket.socket] = {}
        self._rdev = rdev
        self._rport = rport
        self.BUFFER_SIZE = 4096
        self.DELAY = 0.05

    def main_loop(self) -> None:
        while True:
            readable, _, _ = select.select(self.input_list, [], [], self.DELAY)
            for sock in readable:
                if sock == self._server:
                    self.on_accept()
                else:
                    self.on_recv(sock)

    def on_accept(self) -> None:
        client_socket, addr = self._server.accept()
        client_socket.setblocking(False)
        device_socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        device_socket.setblocking(False)
        try:
            device_socket.connect((self._rdev, self._rport))
        except BlockingIOError:
            pass
        self.input_list.append(client_socket)
        self.input_list.append(device_socket)
        self.channel[client_socket] = device_socket
        self.channel[device_socket] = client_socket

    def on_recv(self, sock: socket.socket) -> None:
        try:
            data = sock.recv(self.BUFFER_SIZE)
            if not data:
                self.on_close(sock)
                return
            partner = self.channel.get(sock)
            if partner:
                partner.sendall(data)
        except:
            self.on_close(sock)

    def on_close(self, sock: socket.socket) -> None:
        partner = self.channel.pop(sock, None)
        if partner:
            self.channel.pop(partner, None)
            if partner in self.input_list:
                self.input_list.remove(partner)
            try:
                partner.shutdown(socket.SHUT_RDWR)
            except:
                pass
            partner.close()
        if sock in self.input_list:
            self.input_list.remove(sock)
        try:
            sock.shutdown(socket.SHUT_RDWR)
        except:
            pass
        sock.close()

