class ParameterServer:
    def __init__(self, buffer_dir: str, drain_dir: str, capacity: int, force_sequential: bool):
        self.buffer_dir = buffer_dir
        self.drain_dir = drain_dir
        self.capacity = capacity
        self.force_sequential = force_sequential
        self.dir_lock = Lock()
        self.param_lock = Lock()
        self.buffer_size = 0
        self.submission_id = 0
        self.mortal_param = None
        self.dqn_param = None
        self.param_version = 0
        self.idle_param_version = 0

