                if not data:
                    return
                msg = pickle.loads(data)
                action = msg.get('action')
                try:
                    if action == 'get_param':
                        response = self.handle_get_param(msg)
                    elif action == 'submit_replay':
                        response = self.handle_submit_replay(msg)
                    elif action == 'submit_param':
                        response = self.handle_submit_param(msg)
                    elif action == 'drain':
                        response = self.handle_drain()
                    else:
                        response = {'error': 'Unknown action'}
                except Exception as e:
                    response = {'error': str(e)}
                handler.request.sendall(pickle.dumps(response))

        class ThreadedServer(socketserver.ThreadingMixIn, socketserver.TCPServer):
            daemon_threads = True
            allow_reuse_address = True

        server = ThreadedServer((host, port), ThreadedHandler)
        Thread(target=server.serve_forever, daemon=True).start()

    def handle_get_param(self, msg: dict) -> dict:
        with self.param_lock:
            param_type = msg.get('param_type')
            if param_type not in ('mortal', 'dqn'):
                return {'error': 'Invalid param type'}
            params = self.mortal_param if param_type == 'mortal' else self.dqn_param
            if params is None:
                return {'error': 'Params not initialized'}
            return {
                'params': params,
                'version': self.param_version
            }

    def handle_submit_replay(self, msg: dict) -> dict:
        replay_data = msg.get('replay_data')
        if not replay_data:
            return {'error': 'No replay data provided'}
        with self.dir_lock:
            sub_id = self.submission_id
            filename = os.path.join(self.buffer_dir, f'replay_{sub_id}.pkl')
            with open(filename, 'wb') as f:
                pickle.dump(replay_data, f)
            self.submission_id += 1
            self.buffer_size += 1
            if self.buffer_size > self.capacity:
                oldest = sub_id - (self.buffer_size - self.capacity) + 1
                for i in range(oldest):
                    old_file = os.path.join(self.buffer_dir, f'replay_{i}.pkl')
                    if os.path.exists(old_file):
                        os.remove(old_file)
                self.buffer_size = max(0, self.buffer_size - oldest)
            return {'submission_id': sub_id}

    def handle_submit_param(self, msg: dict) -> dict:
        param_type = msg.get('param_type')
        new_params = msg.get('params')
        version = msg.get('version')
        with self.param_lock:
            if self.force_sequential and version != self.idle_param_version:
                return {'error': 'Version mismatch in sequential mode'}
            if param_type == 'mortal':
                self.mortal_param = new_params
            elif param_type == 'dqn':
                self.dqn_param = new_params
            else:
                return {'error': 'Invalid param type'}
            self.param_version += 1
            return {'version': self.param_version}

    def handle_drain(self) -> dict:
        with self.dir_lock:
            if not os.path.exists(self.drain_dir):
                os.makedirs(self.drain_dir)
            moved_files = 0
            for filename in os.listdir(self.buffer_dir):
                src = os.path.join(self.buffer_dir, filename)
                if os.path.isfile(src):
                    shutil.move(src, self.drain_dir)
                    moved_files += 1
            self.buffer_size = max(0, self.buffer_size - moved_files)
            return {'moved': moved_files}

