class TCPRequestHandler(socketserver.BaseRequestHandler):
    def get_data(self) -> bytes:
        size_bytes = b''
        while len(size_bytes) < 2:
            chunk = self.request.recv(2 - len(size_bytes))
            if not chunk:
                raise ConnectionResetError("Failed to receive data size")
            size_bytes += chunk
        size = struct.unpack('!H', size_bytes)[0]
        data = b''
        while len(data) < size:
            chunk = self.request.recv(size - len(data))
            if not chunk:
                raise ConnectionResetError("Client disconnected during data transfer")
            data += chunk
        return data

    def send_data(self, data: bytes) -> None:
        if len(data) > 0xFFFF:
            raise ValueError("Data exceeds maximum allowed size of 65535 bytes")
        self.request.sendall(struct.pack('!H', len(data)) + data)

