                sock.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, 1)
                sock.bind(('', 1900))
                mreq = struct.pack('4s4s', socket.inet_aton('239.255.255.250'), socket.inet_aton(ip))
                sock.setsockopt(socket.IPPROTO_IP, socket.IP_ADD_MEMBERSHIP, mreq)
                sock.setsockopt(socket.IPPROTO_IP, socket.IP_MULTICAST_IF, socket.inet_aton(ip))
                self.sock_list.append(sock)
            except:
                pass
        if not self.sock_list:
            try:
                self.sock = socket.socket(socket.AF_INET, socket.SOCK_DGRAM, socket.IPPROTO_UDP)
                self.sock.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, 1)
                self.sock.bind(('', 1900))
                mreq = struct.pack('4sL', socket.inet_aton('239.255.255.250'), socket.INADDR_ANY)
                self.sock.setsockopt(socket.IPPROTO_IP, socket.IP_ADD_MEMBERSHIP, mreq)
                self.sock_list.append(self.sock)
            except:
                return
        self.running = True
        self.ssdp_thread.start()

    def stop(self, byebye: bool):
        self.sending_byebye = byebye
        self.running = False
        self.ssdp_thread.join()
        self.shutdown()

    def run(self):
        while self.running:
            readable, _, _ = select.select(self.sock_list, [], [], 1)
            for sock in readable:
                data, addr = sock.recvfrom(65507)
                self.datagram_received(data, addr)

    def datagram_received(self, data: bytes, host_port: tuple):
        headers = {}
        try:
            data_str = data.decode('utf-8', errors='replace')
        except:
            data_str = ''
        lines = data_str.split('\r\n') if data_str else []
        if not lines:
            return
        method_line = lines[0].split()
        if not method_line:
            return
        if method_line[0] == 'M-SEARCH' and 'HTTP/1.1' in method_line[-1]:
            for line in lines[1:]:
                if not line.strip():
                    continue
                key_value = line.split(':', 1)
                if len(key_value) == 2:
                    key = key_value[0].strip().upper()
                    value = key_value[1].strip()
                    headers[key] = value
            self.discovery_request(headers, host_port)

    def discovery_request(self, headers: dict, host_port: tuple):
        man = headers.get('MAN', '')
        st = headers.get('ST', '')
        if man != '"ssdp:discover"' or not st:
            return
        responses = []
        if st == 'ssdp:all':
            responses = list(self.known.keys())
        else:
            for usn, service in self.known.items():
                if service['st'] == st:
                    responses.append(usn)
        for usn in responses:
            service = self.known[usn]
            response = "HTTP/1.1 200 OK\r\n"
            response += f"Location: {service['location']}\r\n"
            response += f"ST: {service['st']}\r\n"
            response += f"USN: {usn}\r\n"
            if 'server' in service and service['server']:
                response += f"Server: {service['server']}\r\n"
            if 'cache_control' in service and service['cache_control']:
                response += f"Cache-Control: {service['cache_control']}\r\n"
            response += "\r\n"
            self.send_it(response, host_port)

    def get_subnet_ip(self, ip: str, mask: str) -> list:
        ip_num = struct.unpack('!I', socket.inet_aton(ip))[0]
        mask_num = struct.unpack('!I', socket.inet_aton(mask))[0]
        subnet_num = ip_num & mask_num
        subnet_ip = socket.inet_ntoa(struct.pack('!I', subnet_num))
        return [subnet_ip]

    def shutdown(self):
        if self.sending_byebye:
            for usn in list(self.known.keys()):
                self.do_byebye(usn)
        for sock in self.sock_list:
            sock.close()
        self.sock_list.clear()
        if self.sock:
            self.sock.close()
            self.sock = None

    def register(self, usn: str, st: str, location: str, server: str, cache_control: str):
        self.known[usn] = {
            'st': st,
            'location': location,
            'server': server,
            'cache_control': cache_control
        }

    def unregister(self, usn: str):
        if usn in self.known:
            if self.sending_byebye:
                self.do_byebye(usn)
            del self.known[usn]

    def do_byebye(self, usn: str):
        notify = "NOTIFY * HTTP/1.1\r\n"
        notify += "Host: 239.255.255.250:1900\r\n"
        notify += "NT: ssdp:byebye\r\n"
        notify += f"USN: {usn}\r\n"
        notify += "\r\n"
        self.send_it(notify, ('239.255.255.250', 1900))

    def send_it(self, response: str, destination: tuple):
        data = response.encode('utf-8')
        for sock in self.sock_list:
            try:
                sock.sendto(data, destination)
            except:
                pass

