class SSDPDiscover:
    SSDP_ADDRESS = '239.255.255.250'
    SSDP_PORT = 1900
    SSDP_MX = 3
    SSDP_TTL = 2
    SSDP_AMOUNT = 3
    MSEARCH_PORT = 0
    MSEARCH_MSG = 'M-SEARCH * HTTP/1.1\r\nHOST: 239.255.255.250:1900\r\nMAN: "ssdp:discover"\r\nMX: {mx}\r\nST: ssdp:all\r\n\r\n'
    BUFFER_SIZE = 4096
    USE_SINGLE_SOCKET = False

    def __init__(self, cb_on_device_response, host=None):
        self.cb_on_device_response = cb_on_device_response
        self.host = host
        self.addresses = []
        self.refresh_addresses()

    def refresh_addresses(self):
        self.addresses = []
        try:
            host_info = socket.gethostbyname_ex(socket.gethostname())
            self.addresses = host_info[2]
        except socket.error:
            pass
        self.addresses = [ip for ip in self.addresses if not ip.startswith('127.')]
        if not self.addresses:
            self.addresses.append('0.0.0.0')

    def search(self, ssdp_ttl=None, ssdp_mx=None, ssdp_amount=None):
        if ssdp_ttl is None:
            ssdp_ttl = self.SSDP_TTL
        if ssdp_mx is None:
            ssdp_mx = self.SSDP_MX
        if ssdp_amount is None:
            ssdp_amount = self.SSDP_AMOUNT
        if self.USE_SINGLE_SOCKET:
            bind_host = self.host or self.addresses[0]
            self._search(bind_host, ssdp_ttl, ssdp_mx, ssdp_amount)
        else:
            for addr in self.addresses:
                self._search(addr, ssdp_ttl, ssdp_mx, ssdp_amount)

    def _search(self, host, ssdp_ttl, ssdp_mx, ssdp_amount):
        sock = socket.socket(socket.AF_INET, socket.SOCK_DGRAM, socket.IPPROTO_UDP)
        sock.settimeout(ssdp_mx)
        try:
            sock.bind((host, self.MSEARCH_PORT))
        except socket.error:
            sock.close()
            return
        ttl = struct.pack('B', ssdp_ttl)
        sock.setsockopt(socket.IPPROTO_IP, socket.IP_MULTICAST_TTL, ttl)
        sock.setsockopt(socket.IPPROTO_IP, socket.IP_MULTICAST_IF, socket.inet_aton(host))
        for _ in range(ssdp_amount):
            self._send_discover(sock, ssdp_mx)
            while True:
                try:
                    response, addr = sock.recvfrom(self.BUFFER_SIZE)
                    self.cb_on_device_response(response, addr)
                except socket.timeout:
                    break
        sock.close()

    def _send_discover(self, sock, ssdp_mx):
        msg = self.MSEARCH_MSG.format(mx=ssdp_mx)
        sock.sendto(msg.encode('utf-8'), (self.SSDP_ADDRESS, self.SSDP_PORT))

