        self._config = config
        self._dns_resolver = dns_resolver
        self._is_local = is_local
        self._sockets = set()
        self._client_addr_map = {}
        self._server_socket = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
        self._server_socket.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, 1)
        self._listen_addr = config.get('listen_address', '0.0.0.0')
        self._listen_port = config.get('listen_port', 0)
        self._server_socket.bind((self._listen_addr, self._listen_port))
        self._closed = False

    def _handle_server(self) -> None:
        while not self._closed:
            try:
                data, addr = self._server_socket.recvfrom(65535)
                remote_host = self._config.get('remote_address')
                remote_port = self._config.get('remote_port', 0)
                if not remote_host or not remote_port:
                    continue
                def resolve_callback(error: str, ip: str) -> None:
                    self._handle_server_dns_resolved(error, (ip, remote_port), remote_host, (data, addr))
                self._dns_resolver.resolve(remote_host, resolve_callback)
