            client_addr = self._client_addr_map.get(sock)
            if not client_addr:
                return
            self._server_socket.sendto(data, client_addr)
        except Exception:
            pass

    def close(self) -> None:
        self._closed = True
        if self._server_socket:
            self._server_socket.close()
            self._server_socket = None
        for sock in self._sockets:
            sock.close()
        self._sockets.clear()
        self._client_addr_map.clear()

