        thread.start()

    def thread_callback(self):
        self.socket = websocket.WebSocketApp(
            self.url,
            on_message=self.message_callback,
            on_open=self.open_callback,
            on_close=self.close_callback
        )
        self.socket.run_forever()

    def send(self, command, callback=None, options=None):
        with self.id_lock:
            cmd_id = self.next_id
            self.next_id += 1
        payload = {'id': cmd_id, 'command': command}
        if options:
            payload.update(options)
        with self.commands_lock:
            self.commands[cmd_id] = callback
        self.socket.send(json.dumps(payload))

    def subscribe(self, notification, callback):
        with self.notifications_lock:
            self.notifications[notification] = callback

    def unsubscribe(self, notification):
        with self.notifications_lock:
            self.notifications.pop(notification, None)

    def message_callback(self, ws, message):
        try:
            data = json.loads(message)
        except json.JSONDecodeError:
            return
        if 'id' in data:
            cmd_id = data['id']
            callback = None
            with self.commands_lock:
                if cmd_id in self.commands:
                    callback = self.commands.pop(cmd_id)
            if callback:
                self.to_main_thread(callback, (data,))
        elif 'notification' in data:
            name = data['notification']
            callback = None
            with self.notifications_lock:
                if name in self.notifications:
                    callback = self.notifications[name]
            if callback:
                self.to_main_thread(callback, (data,))

    def open_callback(self, ws):
        if self.on_open:
            self.to_main_thread(self.on_open, ())

    def close_callback(self, ws):
        if self.on_close:
            self.to_main_thread(self.on_close, ())

    def to_main_thread(self, f, args):
        with self.callback_cond:
            self.callback_queue.append((f, args))
            self.callback_cond.notify()

    def process_callbacks(self):
        while True:
            with self.callback_cond:
                while not self.callback_queue:
                    self.callback_cond.wait()
                tasks = self.callback_queue[:]
                self.callback_queue.clear()
            for f, args in tasks:
                f(*args)

