from typing import Any
import time

class RpcClient(ABC):
    HEARTBEAT_TIMEOUT = 5

    def __init__(self):
        self._context = zmq.Context()
        self._socket_sub = self._context.socket(zmq.SUB)
        self._socket_sub.setsockopt(zmq.TCP_KEEPALIVE, 1)
        self._socket_sub.setsockopt(zmq.TCP_KEEPALIVE_IDLE, 60)
        self._socket_sub.setsockopt(zmq.TCP_KEEPALIVE_INTVL, 10)
        self._socket_sub.setsockopt(zmq.LINGER, 0)
        self._active = False
        self._thread = Thread(target=self.run)
        self._lock = Lock()
        self._last_received_ping = 0.0

    def start(self, sub_address: str) -> None:
        with self._lock:
            if self._active:
                return
            self._active = True
            self._socket_sub.connect(sub_address)
            self.subscribe_topic('PING')
            self._last_received_ping = time.time()
        self._thread.start()

    def stop(self) -> None:
        with self._lock:
            if not self._active:
                return
            self._active = False
        self._socket_sub.close()
        self._context.term()

    def join(self) -> None:
        self._thread.join()

    def run(self) -> None:
        while self._active:
            try:
                if self._socket_sub.poll(1000):
                    message = self._socket_sub.recv_multipart()
                    if message:
                        topic = message[0].decode()
                        data = message[1] if len(message) > 1 else b''
                        if topic == 'PING':
                            self._last_received_ping = time.time()
                        else:
                            self.callback(topic, data)
                else:
                    if self._last_received_ping != 0.0 and (time.time() - self._last_received_ping) > self.HEARTBEAT_TIMEOUT:
                        self.on_disconnected()
            except Exception as e:
                if self._active:
                    self.on_disconnected()
                break

    @abstractmethod
    def callback(self, topic: str, data: Any) -> None:
        pass

    def subscribe_topic(self, topic: str) -> None:
        self._socket_sub.setsockopt(zmq.SUBSCRIBE, topic.encode())

    def on_disconnected(self) -> None:
        print("Warning: Disconnected from publisher")

