from typing import Any, Generator

class ZmqManager:
    def __init__(self, url: str = 'zmq+tcp://localhost:5555+5556', channel: str = 'socketio', write_only: bool = False) -> None:
        self.context = zmq.Context()
        _, address = url.split('://', 1)
        host_part, ports_part = address.split(':', 1)
        push_port, sub_port = ports_part.split('+', 1)

        self.sink = self.context.socket(zmq.PUSH)
        self.sink.connect(f"tcp://{host_part}:{push_port}")

        self.sub = None
        if not write_only:
            self.sub = self.context.socket(zmq.SUB)
            self.sub.connect(f"tcp://{host_part}:{sub_port}")
            self.sub.setsockopt(zmq.SUBSCRIBE, b'')

        self.channel = channel

    def _publish(self, data: Any) -> None:
        payload = (self.channel, data)
        self.sink.send(pickle.dumps(payload))

    def zmq_listen(self) -> Generator[bytes, None, None]:
        while True:
            if self.sub is None:
                raise RuntimeError("SUB socket not available")
            msg = self.sub.recv()
            yield msg

    def _listen(self) -> Generator[Any, None, None]:
        for raw in self.zmq_listen():
            channel, data = pickle.loads(raw)
            if channel == self.channel:
                yield data
