import zmq
import numpy as np
import pickle
import time
from collections import namedtuple

Response = namedtuple('Response', ['id', 'content'])

class BertClient:
    def __init__(self, ip='localhost', port=5555, port_out=5556, identity=None, timeout=-1):
        self.context = zmq.Context()
        self.sender = self.context.socket(zmq.PUSH)
        self.sender.connect(f'tcp://{ip}:{port}')
        self.identity = identity.encode() if isinstance(identity, str) else (identity or self._generate_identity())
        self.sender.setsockopt(zmq.IDENTITY, self.identity)
        self.receiver = self.context.socket(zmq.SUB)
        self.receiver.connect(f'tcp://{ip}:{port_out}')
        self.receiver.setsockopt(zmq.SUBSCRIBE, self.identity)
        self.timeout = timeout
        if timeout != -1:
            self.receiver.setsockopt(zmq.RCVTIMEO, timeout)
        self.sender.setsockopt(zmq.SNDTIMEO, timeout)
        self.request_id = 0
        self.pending_request = set()

    def _generate_identity(self):
        import uuid
        return uuid.uuid4().bytes

    def close(self):
        self.sender.close()
        self.receiver.close()
        self.context.term()

    def _send(self, msg, msg_len=0):
        self.sender.send_multipart([self.identity, str(self.request_id).encode()] + msg)

    def _recv(self):
        parts = self.receiver.recv_multipart()
        req_id = parts[1].decode()
        content = parts[2:]
        return Response(id=req_id, content=content)

    def _recv_ndarray(self):
        response = self._recv()
        try:
            array = pickle.loads(response.content[0])
        except Exception:
            array = None
        self.pending_request.discard(response.id)
        return Response(id=response.id, content=array)

    def encode(self, texts, blocking=True):
        self.request_id += 1
        req_id = str(self.request_id)
        self.pending_request.add(req_id)
        try:
            data = [pickle.dumps(texts)]
        except Exception:
            self.pending_request.discard(req_id)
            return None
        try:
            self._send(data)
        except zmq.Again:
            self.pending_request.discard(req_id)
            return None
        if blocking:
            start_time = time.time()
            while True:
                try:
                    resp = self._recv_ndarray()
                    if resp.id == req_id:
                        return resp.content
                except zmq.Again:
                    if self.timeout != -1:
                        elapsed = (time.time() - start_time) * 1000
                        if elapsed >= self.timeout:
                            break
                    continue
                except Exception:
                    break
            self.pending_request.discard(req_id)
            return None
        return None

    def fetch(self, delay=0.0):
        while True:
            try:
                resp = self._recv_ndarray()
                yield resp
            except zmq.Again:
                if delay > 0.0:
                    time.sleep(delay)
                else:
                    break

    def fetch_all(self, sort=True, concat=False):
        responses = list(self.fetch())
        if sort:
            responses.sort(key=lambda x: int(x.id))
        arrays = [r.content for r in responses]
        if concat:
            return np.concatenate(arrays) if arrays else np.array([])
        return arrays

