from threading import Thread
from collections import deque
import pickle
import zlib

class NetGear:
    def __init__(self, address=None, port=None, protocol='tcp', pattern=2, receive_mode=False, logging=False, **options):
        self.__logging = logging
        self.__msg_context = zmq.Context()
        self.__receive_mode = receive_mode
        self.__pattern = pattern
        self.__terminate = False
        self.__subscriber_timeout = options.get('subscriber_timeout', 5)
        self.__queue = deque()
        self.__thread = None
        self.__compression = options.get('compression', False)

        if pattern == 2:
            sock_type = zmq.SUB if receive_mode else zmq.PUB
        else:
            raise ValueError("Unsupported pattern")

        self.__msg_socket = self.__msg_context.socket(sock_type)

        if receive_mode:
            self.__msg_socket.setsockopt(zmq.SUBSCRIBE, options.get('subscribe', b''))
            self.__msg_socket.setsockopt(zmq.RCVTIMEO, self.__subscriber_timeout * 1000)

        if receive_mode:
            endpoint = f"{protocol}://{address}:{port}"
            self.__msg_socket.connect(endpoint)
        else:
            endpoint = f"{protocol}://*:{port}"
            self.__msg_socket.bind(endpoint)

        if receive_mode:
            self.__thread = Thread(target=self.__recv_handler, daemon=True)
            self.__terminate = False
            self.__thread.start()

    def __recv_handler(self):
        while not self.__terminate:
            try:
                raw = self.__msg_socket.recv()
                if self.__compression:
                    raw = zlib.decompress(raw)
                frame, message = pickle.loads(raw)
                self.__queue.append(frame)
            except zmq.Again:
                continue
            except Exception as e:
                if self.__logging:
                    print(f"Error: {e}")
                break

    def recv(self):
        if not self.__receive_mode:
            raise RuntimeError("Invalid recv mode")
        return self.__queue.popleft() if self.__queue else None

    def send(self, frame, message=None):
        if self.__receive_mode:
            raise RuntimeError("Invalid send mode")
        try:
            data = pickle.dumps((frame, message))
            if self.__compression:
                data = zlib.compress(data)
            self.__msg_socket.send(data)
        except Exception as e:
            if self.__logging:
                print(f"Send error: {e}")

    def close(self, kill=False):
        self.__terminate = True
        if self.__receive_mode and self.__thread:
            self.__thread.join()
        self.__msg_socket.close()
