        self.recv_from_detokenization = self.context.socket(zmq.SUB)
        self.recv_from_detokenization.setsockopt(zmq.SUBSCRIBE, b'')
        if zmq_mode == 'connect':
            endpoint = f'tcp://localhost:{detokenization_pub_port}'
            self.recv_from_detokenization.connect(endpoint)
        elif zmq_mode == 'bind':
            endpoint = f'tcp://*:{detokenization_pub_port}'
            self.recv_from_detokenization.bind(endpoint)
