def server(channel_ip=None, channel_port=5555):
    context = zmq.Context()
    socket = context.socket(zmq.REP)
    bind_address = f"tcp://{channel_ip}:{channel_port}" if channel_ip else f"tcp://*:{channel_port}"
    socket.bind(bind_address)

    remaining_entries = ["task1", "task2", "task3"]
    running_workers = 0

    try:
        while True:
            message = socket.recv_multipart()
            if not message:
                continue
            command = message[0]

            if command == b'GET_TASK':
                if remaining_entries:
                    task = remaining_entries.pop(0)
                    socket.send_pyobj(task)
                    running_workers += 1
                else:
                    socket.send(b'NO_TASK')

            elif command == b'SUCCESS':
                if len(message) >= 2:
                    running_workers -= 1
                socket.send(b'ACK')

            elif command == b'FAILURE':
                if len(message) >= 2:
                    task_id = message[1].decode()
                    remaining_entries.append(task_id)
                    running_workers -= 1
                socket.send(b'ACK')

            else:
                socket.send(b'ERROR')

            if not remaining_entries and running_workers == 0:
                break

    except KeyboardInterrupt:
        pass
    finally:
        socket.close()
        context.term()

