class zmqHandler(Handler):
    def __init__(self, config=None):
        Handler.__init__(self, config)

        if not zmq:
            self.log.error('zmq import failed. Handler disabled')
            self.enabled = False
            return

        self.context = None
        self.socket = None
        self.port = int(self.config['port'])
        self._bind()

