        if not zmq:
            return
        self.context = zmq.Context()
        self.socket = self.context.socket(zmq.PUB)
        self.socket.bind("tcp://*:%i" % self.port)

    def process(self, metric):
        if not zmq:
            return
        self.socket.send("%s" % str(metric))

