    def _get_task_methods(self, method_name: str) -> Dict[str, Any]:
        return {self.method_name_to_task[method_name]: getattr(self, f"_{method_name}_handler")}

def _do_serve(service_impl: ModelResponse, port: int, interceptors: List[grpc.ServerInterceptor] = None) -> None:
    server = grpc.server(ThreadPoolExecutor(), interceptors=interceptors)
    service_class = type(service_impl)
    service_class.add_to_server(service_class, server)
    server.add_insecure_port(f"[::]:{port}")
    server.start()
    service_impl.get_stop_event().wait()
    server.stop(0)

