import httpx
import hmac
import hashlib
import json
from dataclasses import dataclass
from typing import Dict, Any, Optional

@dataclass
class ExternalActionResponse:
    success: bool
    data: Optional[Dict[str, Any]]
    error: Optional[str]
    status_code: int

class ExternalActionsRequester:
    def __init__(self, url: str) -> None:
        self.url = url

    async def send_request(self, payload: Dict[str, Any], signature_secret: str, additional_payload_values: Dict[str, Any] = {}, additional_headers: Dict[str, str] = {}, transport: httpx.AsyncHTTPTransport = httpx.AsyncHTTPTransport(retries=2)) -> ExternalActionResponse:
        full_payload = {**payload, **additional_payload_values}
        payload_bytes = json.dumps(full_payload).encode('utf-8')
        signature = self._encode_payload(payload_bytes, signature_secret)
        headers = {'X-Signature': signature, **additional_headers}

        try:
            async with httpx.AsyncClient(transport=transport) as client:
                response = await client.post(self.url, content=payload_bytes, headers=headers)
                response.raise_for_status()
                try:
                    response_data = response.json()
                except ValueError:
                    response_data = {}
        except httpx.HTTPStatusError as e:
            response = e.response
            try:
                response_data = response.json()
            except ValueError:
                response_data = {}
        except httpx.RequestError as e:
            return ExternalActionResponse(success=False, data=None, error=str(e), status_code=0)

        return self._validate_response(response_data)

    def _encode_payload(self, payload: bytes, signature_secret: str) -> str:
        hmac_obj = hmac.new(signature_secret.encode('utf-8'), payload, hashlib.sha256)
        return hmac_obj.hexdigest()

    def _validate_response(self, response: Dict[str, Any]) -> ExternalActionResponse:
        error = response.get('error')
        success = error is None
        status_code = response.get('status_code', 500 if error else 200)
        return ExternalActionResponse(
            success=success,
            data=response.get('data'),
            error=error,
            status_code=status_code
        )

