from http.client import HTTPConnection, HTTPSConnection
from urllib.parse import urlparse

class HTTPConnectionPool:
    scheme = 'http'
    ConnectionCls = HTTPConnection
    QueueCls = queue.LifoQueue

    def __init__(self, host: str, port: Optional[int] = None, timeout: Union[float, None] = None, maxsize: int = 1, block: bool = False, headers: Optional[Mapping[str, str]] = None, retries: Union[bool, int, None] = None, _proxy: Optional[urlparse] = None, _proxy_headers: Optional[Mapping[str, str]] = None, **conn_kw: Any) -> None:
        self.host = host
        self.port = port
        self.timeout = timeout
        self.maxsize = maxsize
        self.block = block
        self.headers = headers or {}
        self.retries = retries
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers or {}
        self.conn_kw = conn_kw
        self.pool = self.QueueCls(maxsize=maxsize)
        if self.proxy and self.proxy.scheme == 'https':
            self.scheme = 'https'
            self.ConnectionCls = HTTPSConnection
        elif self.scheme == 'https':
            self.ConnectionCls = HTTPSConnection
        else:
            self.scheme = 'http'
            self.ConnectionCls = HTTPConnection

    def _new_conn(self) -> HTTPConnection:
        if self.proxy:
            host = self.proxy.hostname
            port = self.proxy.port
        else:
            host = self.host
            port = self.port
        conn = self.ConnectionCls(host=host, port=port, timeout=self.timeout, **self.conn_kw)
        return conn

    def _get_conn(self, timeout: Optional[float] = None) -> HTTPConnection:
        try:
            conn = self.pool.get(block=self.block, timeout=timeout)
        except queue.Empty:
            conn = None
        if conn is None:
            conn = self._new_conn()
        return conn

    def _put_conn(self, conn: Optional[HTTPConnection]) -> None:
        if conn and not getattr(conn, 'is_closed', False):
            self.pool.put(conn)

    def _make_request(self, conn: HTTPConnection, method: str, url: str, body: Optional[Any] = None, headers: Optional[Mapping[str, str]] = None, retries: Union[int, None] = None, timeout: Union[float, None] = None, **response_kw: Any) -> Any:
        if self.proxy and self.scheme == 'https':
            conn.putrequest('CONNECT', f"{self.host}:{self.port}")
            for header, value in self.proxy_headers.items():
                conn.putheader(header, value)
            conn.endheaders()
            resp = conn.getresponse()
            if resp.status != 200:
                raise Exception(f"Proxy tunnel failed: {resp.status} {resp.reason}")
        conn.putrequest(method, url)
        for header, value in self.headers.items():
            conn.putheader(header, value)
        if headers:
            for header, value in headers.items():
                conn.putheader(header, value)
        conn.endheaders()
        if body:
            conn.send(body)
        return conn.getresponse()

    def urlopen(self, method: str, url: str, body: Optional[Any] = None, headers: Optional[Mapping[str, str]] = None, retries: Optional[int] = None, redirect: bool = True, assert_same_host: bool = True, timeout: Union[float, None] = None, pool_timeout: Optional[int] = None, **response_kw: Any) -> Any:
        retries = retries if retries is not None else self.retries if isinstance(self.retries, int) else 0
        redirect_location = url
        for attempt in range(retries + 1):
            conn = self._get_conn(timeout=pool_timeout)
            try:
                response = self._make_request(conn, method, redirect_location, body, headers, retries, timeout, **response_kw)
                if 300 <= response.status < 400 and redirect:
                    location = response.getheader('Location')
                    if location:
                        redirect_location = location
                        continue
                return response
            except Exception as e:
                if attempt < retries:
                    continue
                raise
            finally:
                self._put_conn(conn)
        raise Exception("Max retries exceeded")

    def _close_pool_connections(self, pool: queue.Queue) -> None:
        while not pool.empty():
            conn = pool.get_nowait()
            if conn:
                conn.close()

    def close(self) -> None:
        self._close_pool_connections(self.pool)
        self.pool = self.QueueCls(maxsize=self.maxsize)

