import os
import requests

def google_search(keywords, paper_num=1, search_type='web'):
    if isinstance(keywords, list):
        query = ' '.join(str(k) for k in keywords)
    else:
        query = str(keywords)

    api_key = os.environ.get('GOOGLE_API_KEY')
    cse_id = os.environ.get('GOOGLE_CSE_ID')

    if not api_key or not cse_id:
        error_msg = "API key or CSE ID not configured. Please set GOOGLE_API_KEY and GOOGLE_CSE_ID environment variables."
        print(f"Error: {error_msg}")
        return error_msg

    url = 'https://www.googleapis.com/customsearch/v1'
    params = {
        'key': api_key,
        'cx': cse_id,
        'q': query,
        'start': (paper_num - 1) * 10 + 1
    }
    if search_type == 'image':
        params['searchType'] = 'image'

    try:
        response = requests.get(url, params=params)
        print(f"Status Code: {response.status_code}")
        print(f"Response Body: {response.text[:500]}...")
        if not response.ok:
            error_msg = f"HTTP error {response.status_code}: {response.text}"
            print(f"Error: {error_msg}")
            return error_msg
    except requests.exceptions.RequestException as e:
        error_msg = f"Request failed: {str(e)}"
        print(f"Error: {error_msg}")
        return error_msg

    try:
        data = response.json()
    except ValueError:
        error_msg = "Failed to parse response JSON."
        print(f"Error: {error_msg}")
        return error_msg

    if 'error' in data:
        error_msg = f"API Error: {data['error'].get('message', 'Unknown error')}"
        print(f"Error: {error_msg}")
        return error_msg

    items = data.get('items', [])
    if not items:
        return "No results found. You may want to check the next page."

    results = []
    for item in items:
        snippet = item.get('snippet', 'No snippet available')
        link = item.get('link', 'No link available')
        date = ''

        if 'pagemap' in item and 'metatags' in item['pagemap'] and len(item['pagemap']['metatags']) > 0:
            metatags = item['pagemap']['metatags'][0]
            date = metatags.get('og:updated_time', '') or metatags.get('article:modified_time', '') or metatags.get('date', '') or metatags.get('dc.date', '') or metatags.get('dc:date', '')
            if date:
                date = date.split('T')[0]

        result = f"{snippet}\n{link}"
        if date:
            result += f"\nDate: {date}"
        results.append(result)

    return "\n\n---\n\n".join(results)

import requests
from bs4 import BeautifulSoup
import os
from urllib.parse import urljoin

def download_xkcd_comic(issue_number):
    base_url = 'https://xkcd.com/'
    comic_url = f'{base_url}{issue_number}/'
    headers = {'User-Agent': 'xkcd-comic-downloader/1.0'}

    try:
        response = requests.get(comic_url, headers=headers)
    except requests.exceptions.RequestException as e:
        return f'Error: Failed to connect to server - {str(e)}'

    if response.status_code != 200:
        return f'Error: Failed to retrieve comic page (status code {response.status_code})'

    soup = BeautifulSoup(response.text, 'html.parser')
    comic_div = soup.find('div', id='comic')
    if not comic_div:
        return 'Error: Comic image container not found'
    img_tag = comic_div.find('img')
    if not img_tag:
        return 'Error: Comic image element not found'
    img_url = img_tag.get('src')
    if not img_url:
        return 'Error: Image source URL not found'

    full_img_url = urljoin(response.url, img_url)

    try:
        img_response = requests.get(full_img_url, stream=True, headers=headers)
        img_response.raise_for_status()
    except requests.exceptions.RequestException as e:
        return f'Error: Failed to download image - {str(e)}'

    filename = os.path.basename(full_img_url.split('?')[0])
    if not filename:
        filename = f'comic_{issue_number}.png'

    with open(filename, 'wb') as f:
        for chunk in img_response.iter_content(chunk_size=8192):
            if chunk:
                f.write(chunk)

    return f'Successfully downloaded {filename}'

