        headers = {
            'Authorization': f'Bearer {self.access_token}',
            'Accept': 'application/json'
        }
        response = requests.get(f'{self.base_url}/user', headers=headers)
        if response.status_code != 200:
            raise Exception('User verification failed')

    def get_orders(self, status: str, start_date: Optional[datetime], end_date: Optional[datetime], limit: int) -> List[Dict]:
        headers = {
            'Authorization': f'Bearer {self.access_token}',
            'Accept': 'application/json'
        }
        params = {'status': status}
        if start_date:
            params['createdStartDate'] = start_date.isoformat()
        if end_date:
            params['createdEndDate'] = end_date.isoformat()
        if limit:
            params['limit'] = limit
        response = requests.get(f'{self.base_url}/orders', headers=headers, params=params)
        if response.status_code != 200:
            raise Exception('Failed to retrieve orders')
        return response.json()

    def acknowledge_order(self, purchase_order_id: str) -> str:
        headers = {
            'Authorization': f'Bearer {self.access_token}',
            'Content-Type': 'application/json',
            'Accept': 'application/json'
        }
        response = requests.post(f'{self.base_url}/orders/{purchase_order_id}/acknowledge', headers=headers)
        if response.status_code != 200:
            raise Exception('Order acknowledgment failed')
        return response.text

    def get_inventory(self, sku_list: List[str] = None) -> List[Dict]:
        headers = {
            'Authorization': f'Bearer {self.access_token}',
            'Accept': 'application/json'
        }
        params = {}
        if sku_list:
            params['sku'] = ','.join(sku_list)
        response = requests.get(f'{self.base_url}/inventory', headers=headers, params=params)
        if response.status_code != 200:
            raise Exception('Failed to retrieve inventory')
        return response.json()

    def update_inventory(self, sku: str, quantity: int) -> str:
        headers = {
            'Authorization': f'Bearer {self.access_token}',
            'Content-Type': 'application/xml',
            'Accept': 'application/xml'
        }
        payload = f"""<?xml version="1.0" encoding="UTF-8"?>
