from queue import LifoQueue, Empty
from http.client import HTTPConnection

class Timeout:
    DEFAULT_TIMEOUT = None

class Retry:
    DEFAULT = None

class HTTPConnectionPool:
    scheme = 'http'
    ConnectionCls = HTTPConnection

    def __init__(self, host, port=None, strict=False, timeout=Timeout.DEFAULT_TIMEOUT, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        self.host = host
        self.port = port
        self.strict = strict
        self.timeout = timeout
        self.retries = retries if retries is not None else Retry.DEFAULT
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers
        self.conn_kw = conn_kw
        self.pool = LifoQueue(maxsize=maxsize)
        self.block = block

    def _new_conn(self):
        return self.ConnectionCls(self.host, self.port, strict=self.strict, timeout=self.timeout, **self.conn_kw)

    def _get_conn(self, timeout=None):
        try:
            conn = self.pool.get(block=self.block, timeout=timeout)
        except Empty:
            conn = self._new_conn()
        return conn

    def _put_conn(self, conn):
        if self.pool.full():
            conn.close()
        else:
            self.pool.put(conn)

    def _make_request(self, conn, method, url, timeout=None, **httplib_request_kw):
        if timeout is not None:
            conn.timeout = timeout
        conn.request(method, url, **httplib_request_kw)
        return conn.getresponse()

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, timeout=None, **response_kw):
        retries = retries if retries is not None else self.retries
        conn = None
        while True:
            try:
                if conn is None:
                    conn = self._get_conn()
                response = self._make_request(conn, method, url, body=body, headers=headers, timeout=timeout, **response_kw)
                if redirect and response.status in (301, 302, 303, 307, 308):
                    if redirect:
                        new_url = response.getheader('Location')
                        if new_url:
                            url = new_url
                            continue
                return response
            except Exception as e:
                if retries and retries > 0:
                    retries -= 1
                    if conn:
                        conn.close()
                    conn = None
                    continue
                else:
                    if conn:
                        conn.close()
                    raise
            finally:
                if conn and not self.pool.full():
                    self._put_conn(conn)

    def close(self):
        while not self.pool.empty():
            conn = self.pool.get()
            conn.close()

