import requests
import xml.etree.ElementTree as ET

class PlexMediaServer:
    TIMEOUT = 10

    def __init__(self, mediaserver_instance):
        self.object = mediaserver_instance

    def make_request(self, uri='/', params=None):
        if params is None:
            params = {}
        url = f"{self.object.base_url}{uri}"
        headers = {
            'X-Plex-Token': self.object.token,
            'Accept': 'application/xml'
        }
        try:
            response = requests.get(url, headers=headers, params=params, timeout=self.TIMEOUT, verify=self.object.verify_ssl)
            response.raise_for_status()
            return response
        except requests.exceptions.RequestException as e:
            raise Exception(f"Request failed: {e}") from e

    def validate(self):
        try:
            response = self.make_request('/')
            if response.status_code != 200:
                raise Exception("Server connection failed")

            libraries_response = self.make_request('/library/sections')
            root = ET.fromstring(libraries_response.content)
            sections = root.findall('Directory')
            configured_libraries = self.object.libraries
            server_libraries = [section.get('title') for section in sections]

            for lib in configured_libraries:
                if lib not in server_libraries:
                    raise Exception(f"Library '{lib}' not found on server")

            return True
        except Exception as e:
            raise Exception(f"Validation failed: {e}") from e

    def update(self):
        try:
            sections_response = self.make_request('/library/sections')
            root = ET.fromstring(sections_response.content)
            sections = root.findall('Directory')
            configured_libraries = self.object.libraries

            for section in sections:
                title = section.get('title')
                if title in configured_libraries:
                    section_id = section.get('key')
                    url = f"{self.object.base_url}/library/sections/{section_id}/refresh"
                    headers = {
                        'X-Plex-Token': self.object.token,
                        'Accept': 'application/xml'
                    }
                    response = requests.post(url, headers=headers, timeout=self.TIMEOUT, verify=self.object.verify_ssl)
                    response.raise_for_status()
            return True
        except Exception as e:
            raise Exception(f"Update failed: {e}") from e

