    try:
        response = requests.get(url)
    except requests.exceptions.RequestException as e:
        raise ValueError("HTTP request failed") from e
    if response.status_code != 200:
        raise ValueError("HTTP request failed")
    json_data = response.json()
    if 'quoteResponse' not in json_data or not json_data['quoteResponse'].get('data'):
        raise ValueError("No data found for the symbol")
    data = json_data['quoteResponse']['data'][0]
