import logging
import requests
from enum import Enum

class SXTApiCallTypes(Enum):
    GET = "GET"
    POST = "POST"
    PUT = "PUT"
    DELETE = "DELETE"

class SXTBaseAPI:
    def __init__(self, access_token: str = '', logger: logging.Logger = None):
        self.api_url = 'https://api.spaceandtime.app'
        self.access_token = access_token
        self.logger = logger or logging.getLogger(__name__)
        self.network_calls_enabled = True
        self.standard_headers = {'Content-Type': 'application/json'}
        self.versions = {}
        self.APICALLTYPE = SXTApiCallTypes

    def call_api(self, endpoint: str, auth_header: bool = True, request_type: str = SXTApiCallTypes.POST.value, header_parms: dict = {}, data_parms: dict = {}, query_parms: dict = {}, path_parms: dict = {}) -> tuple[bool, dict]:
        if not self.network_calls_enabled:
            return True, self.__fakedata__(endpoint)

        url = self.api_url
        if endpoint in self.versions:
            url += f"/{self.versions[endpoint]}"
        url += f"/{endpoint}"

        if path_parms:
            for key, value in path_parms.items():
                url = url.replace(f"{{{key}}}", str(value))

        headers = self.standard_headers.copy()
        if auth_header and self.access_token:
            headers['Authorization'] = f'Bearer {self.access_token}'
        headers.update(header_parms)

        try:
            if request_type == self.APICALLTYPE.GET.value:
                response = requests.get(url, headers=headers, params=query_parms)
            elif request_type == self.APICALLTYPE.POST.value:
                response = requests.post(url, headers=headers, json=data_parms, params=query_parms)
            elif request_type == self.APICALLTYPE.PUT.value:
                response = requests.put(url, headers=headers, json=data_parms, params=query_parms)
            elif request_type == self.APICALLTYPE.DELETE.value:
                response = requests.delete(url, headers=headers, params=query_parms)
            else:
                raise ValueError(f"Unsupported request type: {request_type}")

            self.logger.info(f"API Call: {request_type} {url} - Status Code: {response.status_code}")
            if response.status_code >= 200 and response.status_code < 300:
                return True, response.json()
            else:
                self.logger.error(f"API Error: {response.status_code} - {response.text}")
                return False, {"error": response.text, "status_code": response.status_code}
        except Exception as e:
            self.logger.exception(f"API Exception: {str(e)}")
            return False, {"error": str(e)}

    def __fakedata__(self, endpoint: str) -> dict:
        if endpoint == "auth":
            return {"access_token": "mock_token", "expires_in": 3600}
        elif endpoint == "data":
            return {"items": [{"id": 1, "name": "Test Data"}]}
        return {"error": "No mock data available for endpoint"}
