    if content_type == 'application/json':
        data = request.get_json()
        text = data.get('text')
        bing_key = data.get('bing_key')
        predictions = get_predictions(text, bing_key)
        return jsonify({'predictions': predictions}), 200, {'Content-Type': 'application/json'}
