            csv_data = request.data.decode('utf-8')
            df = pd.read_csv(StringIO(csv_data))
            texts = df['text'].tolist()
        else:
            texts = request.data.decode('utf-8').splitlines()
        predictions_list = []
        for text in texts:
            predictions = get_predictions(text)
            predictions_list.append(predictions)
        output = StringIO()
        writer = csv.writer(output)
        writer.writerows(predictions_list)
        csv_str = output.getvalue()
        return csv_str, 200, {'Content-Type': 'text/csv'}
    else:
        return 'Unsupported content type', 415, {'Content-Type': 'text/plain'}

if __name__ == '__main__':
    app.run(debug=True)

