import os
import pickle

class InferenceServer:
    def __init__(self, models_dirs):
        self.app = Flask(__name__)
        self.app.config['server'] = self
        self.api = Api(self.app)
        self.inferencers = {}
        self._load_models(models_dirs)
        self._setup_routes()

    def _load_models(self, models_dirs):
        for idx, dir_path in enumerate(models_dirs):
            model_path = os.path.join(dir_path, 'model.pkl')
            with open(model_path, 'rb') as f:
                model = pickle.load(f)
            self.inferencers[idx] = {
                'model': model,
                'metadata': {
                    'id': idx,
                    'name': os.path.basename(dir_path)
                }
            }

    def _setup_routes(self):
        self.api.add_resource(self.ModelListEndpoint, '/models')
        self.api.add_resource(self.InferenceEndpoint, '/infer/<int:model_id>')

    def run(self, host='0.0.0.0', port=5000):
        self.app.run(host=host, port=port)

    def resp_json(self, data, code=200, headers=None):
        return Response(
            json.dumps(data, cls=self.NumpyEncoder),
            status=code,
            mimetype='application/json'
        )

    class NumpyEncoder(json.JSONEncoder):
        def default(self, obj):
            if isinstance(obj, np.ndarray):
                return obj.tolist()
            if isinstance(obj, np.float32):
                return float(obj)
            return super().default(obj)

    class ModelListEndpoint(Resource):
        def get(self):
            server = current_app.config['server']
            models = [inferencer['metadata'] for inferencer in server.inferencers.values()]
            return server.resp_json(models, 200)

    class InferenceEndpoint(Resource):
        def post(self, model_id):
            server = current_app.config['server']
            model_info = server.inferencers.get(model_id)
            if not model_info:
                return server.resp_json({'error': 'Model not found'}, 404)
            data = request.get_json()['data']
            result = model_info['model'].predict(data)
            return server.resp_json(result, 200)

