        def ingest_data():
            source_dir = request.args.get('source_dir')
            documents = self._load_documents(source_dir)
            text_splitter = CharacterTextSplitter(chunk_size=1000, chunk_overlap=200)
            docs = text_splitter.split_documents(documents)
            embeddings = GPT4AllEmbeddings(model_name=self.embeddings_model_name)
            db = FAISS.from_documents(docs, embeddings)
            db.save_local(self.persist_directory)
            return jsonify({"status": "Documents ingested and stored"})

        @self.app.route('/get_answer', methods=['POST'])
        def get_answer():
            question = request.json.get('question')
            if not self.llm:
                self._load_model()
            answer = self.llm(question)
            return jsonify({"answer": answer})

        @self.app.route('/download_model', methods=['GET'])
        def download_and_save():
            try:
                self.llm = GPT4All(model=self.model_path, model_type=self.model_type, n_ctx=self.model_n_ctx)
                return jsonify({"status": "Model already exists"})
            except Exception as e:
                GPT4All.download_model(self.model_path)
