                return jsonify({"status": "Model downloaded and loaded"})

    def _load_documents(self, source_dir):
        loader = DirectoryLoader(source_dir, loader_cls=TextLoader)
        return loader.load()

    def _load_single_document(self, file_path):
        return TextLoader(file_path).load()

    def run(self):
        self.app.run(debug=True)

    def _load_model(self):
        self.llm = GPT4All(model=self.model_path, model_type=self.model_type, n_ctx=self.model_n_ctx)

