logging.basicConfig(level=logging.WARNING)

def process_circleci_event(headers, msg):
    event_type = headers.get('Circleci-Event-Type')
    if not event_type:
        return None
    supported_types = ['build', 'pipeline', 'test']
    if event_type not in supported_types:
        return None
    try:
        decoded_data = base64.b64decode(msg['data']).decode('utf-8')
        event_data = json.loads(decoded_data)
    except (base64.binascii.Error, json.JSONDecodeError, UnicodeDecodeError):
        return None
    event_id = event_data.get('id')
    time_created = event_data.get('timestamp')
    signature = headers.get('Circleci-Signature')
    msg_id = msg['message_id']
    metadata = json.dumps(event_data)
    return {
        'event_type': event_type,
        'id': event_id,
        'metadata': metadata,
        'time_created': time_created,
        'signature': signature,
        'msg_id': msg_id,
        'source': 'circleci'
    }

@app.route('/', methods=['POST'])
def index():
    if request.method != 'POST':
        return '', 405
    try:
        data = request.json
    except:
        return '', 400
    if not data or 'message' not in data:
        return '', 400
    message = data['message']
    if 'data' not in message or 'message_id' not in message or 'attributes' not in message:
        return '', 400
    attributes = message['attributes']
    try:
        headers = json.loads(attributes.get('headers', '{}'))
    except:
        logging.warning("Invalid headers in attributes")
        return '', 204
    msg = {
        'data': message['data'],
        'message_id': message['message_id']
    }
    try:
        processed_data = process_circleci_event(headers, msg)
    except Exception as e:
        logging.warning(f"Error processing event: {e}")
        return '', 204
    if not processed_data:
        logging.warning("Unsupported event type")
        return '', 204
    return '', 204

if __name__ == '__main__':
    app.run()

