from datetime import datetime

class FlaskAPI:
    def __init__(self, config):
        self.app = Flask(__name__)
        self.config = config
        self.next_user_id = 1
        self.last_modified = datetime.now().isoformat()
        self.users = {}
        self.deleted_orders = set()
        self._setup_routes()
        self.app.before_request(self.check_auth)

    def _setup_routes(self):
        self.app.route('/spec', methods=['GET'])(self.get_spec)
        self.app.route('/users', methods=['GET'])(self.list_users)
        self.app.route('/users', methods=['POST'])(self.create_user)
        self.app.route('/users/<int:user_id>', methods=['GET'])(self.get_user)
        self.app.route('/users/<int:user_id>', methods=['PUT'])(self.update_user)
        self.app.route('/users/<int:user_id>', methods=['DELETE'])(self.delete_user)
        self.app.route('/orders/<string:order_id>', methods=['DELETE'])(self.delete_order)

    def get_spec(self):
        return jsonify({
            "openapi": "3.0.0",
            "info": {"title": "User API", "version": "1.0"},
            "paths": {
                "/users": {
                    "get": {"summary": "List users"},
                    "post": {"summary": "Create user"}
                },
                "/users/{user_id}": {
                    "get": {"summary": "Get user"},
                    "put": {"summary": "Update user"},
                    "delete": {"summary": "Delete user"}
                },
                "/orders/{order_id}": {
                    "delete": {"summary": "Delete order"}
                }
            }
        })

    def get_user(self, user_id):
        if self.config.simulate_error:
            return jsonify({"error": "Simulated error"}), 500
        if self.config.delay:
            time.sleep(self.config.delay)
        user = self.users.get(user_id)
        if not user:
            return jsonify({"error": "User not found"}), 404
        return jsonify(user)

    def list_users(self):
        if self.config.auth_required and not self.check_auth():
            return jsonify({"error": "Unauthorized"}), 401
        return jsonify(list(self.users.values()))

    def create_user(self):
        if self.config.simulate_error:
            return jsonify({"error": "Simulated error"}), 500
        if self.config.delay:
            time.sleep(self.config.delay)
        data = request.json
        user_id = self.next_user_id
        self.next_user_id += 1
        user = {
            "id": user_id,
            "name": data.get("name"),
            "email": data.get("email")
        }
        self.users[user_id] = user
        self.last_modified = datetime.now().isoformat()
        return jsonify(user), 201

    def update_user(self, user_id):
        if self.config.simulate_error:
            return jsonify({"error": "Simulated error"}), 500
        if self.config.delay:
            time.sleep(self.config.delay)
        user = self.users.get(user_id)
        if not user:
            return jsonify({"error": "User not found"}), 404
        data = request.json
        user.update(data)
        self.last_modified = datetime.now().isoformat()
        return jsonify(user)

    def delete_user(self, user_id):
        if self.config.simulate_error:
            return jsonify({"error": "Simulated error"}), 500
        if self.config.delay:
            time.sleep(self.config.delay)
        user = self.users.pop(user_id, None)
        if not user:
            return jsonify({"error": "User not found"}), 404
        return jsonify({"message": "User deleted"}), 200

    def delete_order(self, order_id):
        if self.config.simulate_error:
            return jsonify({"error": "Simulated error"}), 500
        if self.config.delay:
            time.sleep(self.config.delay)
        self.deleted_orders.add(order_id)
        return jsonify({"message": "Order deleted"}), 200

    def check_auth(self):
        if self.config.auth_required:
            auth = request.headers.get('Authorization')
            if not auth:
                return jsonify({"error": "Unauthorized"}), 401
        return None

