            ctx = self.default_context(papers, render_format='recent',
                                    msg='Showing most recent Arxiv papers:')
            return render_template('main.html', **ctx)

        @self.app.route('/login', methods=['POST'])
        def login():
            if not request.form['username']:
                flash('You have to enter a username')
            elif not request.form['password']:
                flash('You have to enter a password')
            elif self.get_user_id(request.form['username']) is not None:
                user = self.query_db('''select * from user where
                    username = ?''', [request.form['username']], one=True)
                if check_password_hash(user['pw_hash'], request.form['password']):
                    session['user_id'] = self.get_user_id(request.form['username'])
                    flash('User ' + request.form['username'] + ' logged in.')
