                backend.delete_tarball(secret, name, filename[0:-4])

        if not backend.put_blueprint(secret, name, request.data):
            abort(502)

        return MeteredResponse(response='',
                             status=202,
                             content_type='text/plain')

    def get_tarball(self, secret, name, sha):
