from flask import Flask, request, jsonify
import json

app = Flask(__name__)

def load_colors():
    try:
        with open('colors.json', 'r') as f:
            return json.load(f)
    except (FileNotFoundError, json.JSONDecodeError):
        return []

def save_colors(data):
    with open('colors.json', 'w') as f:
        json.dump(data, f)

@app.route('/colors', methods=['GET'])
def get_colors():
    data = load_colors()
    return jsonify(data)

@app.route('/colors/<name>', methods=['GET'])
def get_color(name):
    data = load_colors()
    color = next((item for item in data if item['name'] == name), None)
    if color:
        return jsonify(color)
    else:
        return jsonify({"error": "Color not found"}), 404

@app.route('/colors', methods=['POST'])
def create_color():
    data = request.get_json()
    if not data or 'name' not in data or 'value' not in data:
        return jsonify({"error": "Missing name or value"}), 400
    name = data['name']
    value = data['value']
    colors = load_colors()
    if any(item['name'] == name for item in colors):
        return jsonify({"error": "Color already exists"}), 400
    colors.append({'name': name, 'value': value})
    save_colors(colors)
    return jsonify({'name': name, 'value': value}), 201

@app.route('/colors/<name>', methods=['PUT'])
def update_color(name):
    data = request.get_json()
    if 'value' not in data:
        return jsonify({"error": "Missing value"}), 400
    value = data['value']
    colors = load_colors()
    for color in colors:
        if color['name'] == name:
            color['value'] = value
            save_colors(colors)
            return jsonify(color)
    return jsonify({"error": "Color not found"}), 404

@app.route('/colors/<name>', methods=['DELETE'])
def delete_color(name):
    colors = load_colors()
    for i, color in enumerate(colors):
        if color['name'] == name:
            deleted_color = color
            del colors[i]
            save_colors(colors)
            return jsonify(deleted_color)
    return jsonify({"error": "Color not found"}), 404

if __name__ == '__main__':
    app.run(debug=True)

