        self._ssl_verification_enabled = ssl_verification_enabled
        self.maxtries = 3
        self._tries = 0
        self._delay = 1
        self._closed = False
        self._conn = None
        self._bytes = b''
        self._request_sent = False

    def _connect(self):
        proxy = self._get_proxy_config()
        if proxy:
            self._conn = http.client.HTTPConnection(proxy[0], proxy[1])
        else:
            if self._ssl_enabled:
                context = self._get_ssl_context()
                self._conn = http.client.HTTPSConnection(self._server, self._port, context=context)
            else:
                self._conn = http.client.HTTPConnection(self._server, self._port)

    def _get_proxy_config(self):
        proxy_env = os.getenv('HTTP_PROXY') or os.getenv('http_proxy')
        if proxy_env:
            parsed = urllib.parse.urlparse(proxy_env)
            return (parsed.hostname, parsed.port)
        return None

    def _get_ssl_context(self):
        if self._ssl_verification_enabled:
            return ssl.create_default_context()
        else:
            return ssl._create_unverified_context()

    def write(self, data, reconnect_on=("", 200, 502)):
        if self._closed:
            raise Exception("Connection is closed")
        if not self._isconnected():
            self._connect()
        if not self._request_sent:
            self._send_request()
            self._request_sent = True
        chunk_length = len(data)
        self._conn.send(f"{chunk_length:x}\r\n".encode())
        self._conn.send(data)
        self._conn.send(b"\r\n")
        try:
            response = self._getresponse()
            if response.status in reconnect_on:
                self._reconnect()
        except:
            self._reconnect()

    def _send_request(self):
        self._headers["Transfer-Encoding"] = "chunked"
        self._conn.request("POST", self._url, headers=self._headers)

    def _getresponse(self):
        return self._conn.getresponse()

    def _isconnected(self):
        return self._conn is not None and not self._conn.sock.closed

    def _reconnect(self):
        if self._tries < self.maxtries:
            self._tries += 1
            time.sleep(self._delay)
            self._connect()
            self._request_sent = False
        else:
            raise Exception("Max reconnection attempts reached")

    def close(self):
        self._conn.close()
        self._closed = True
        return self._getresponse()

    def _reset_retries(self):
        self._tries = 0
        self._delay = 1
