        self.app.route('/api/list/<sid>', methods=['GET'])(self.api_list)
        self.app.route('/api/stop/<sid>', methods=['POST'])(self.api_stop)

    def _start_janitor(self):
        self.janitor_thread = threading.Thread(target=self.janitor, daemon=True)
        self.janitor_thread.start()

    def janitor(self):
        while True:
            time.sleep(60)
            current_time = time.time()
            to_remove = []
            for session in self.sessions:
                if current_time - session['timestamp'] > 3600:
                    to_remove.append(session)
            for session in to_remove:
                self.sessions.remove(session)

    def root(self):
        return "DNSTwist API", 200

    def api_scan(self):
        data = flask.request.json
        domain = data.get('domain')
        if not domain:
            return flask.jsonify({'error': 'Domain required'}), 400

        sid = str(uuid.uuid4())
        session = {
            'sid': sid,
            'domain': domain,
            'results': [],
            'status': 'running',
            'timestamp': time.time(),
            'thread': None,
            'stop_event': threading.Event()
        }
        self.sessions.append(session)

        def scan_task():
            try:
                if session['stop_event'].is_set():
                    session['status'] = 'stopped'
                    return
                results = []
                for perm in dnstwist.generate(domain):
                    if session['stop_event'].is_set():
                        session['status'] = 'stopped'
                        return
                    if dnstwist.check(perm):
                        results.append(perm)
                session['results'] = results
                session['status'] = 'completed'
            except Exception as e:
                session['status'] = 'failed'
                print(f"Scan failed: {e}")
            finally:
                session['thread'] = None

        thread = threading.Thread(target=scan_task)
        session['thread'] = thread
        thread.start()

        return flask.jsonify({'sid': sid}), 201

    def api_status(self, sid):
        session = next((s for s in self.sessions if s['sid'] == sid), None)
        if not session:
            return flask.jsonify({'error': 'Session not found'}), 404
        return flask.jsonify({'status': session['status']}), 200

    def api_domains(self, sid):
        session = next((s for s in self.sessions if s['sid'] == sid), None)
        if not session:
            return flask.jsonify({'error': 'Session not found'}), 404
        return flask.jsonify({'domains': session['results']}), 200

    def api_csv(self, sid):
        session = next((s for s in self.sessions if s['sid'] == sid), None)
        if not session:
            return flask.jsonify({'error': 'Session not found'}), 404
        output = io.StringIO()
        writer = csv.writer(output)
        writer.writerow(['Domain'])
        for domain in session['results']:
            writer.writerow([domain])
        return output.getvalue(), 200, {'Content-Type': 'text/csv'}

    def api_json(self, sid):
        session = next((s for s in self.sessions if s['sid'] == sid), None)
        if not session:
            return flask.jsonify({'error': 'Session not found'}), 404
        return flask.jsonify({'domains': session['results']}), 200

    def api_list(self, sid):
        session = next((s for s in self.sessions if s['sid'] == sid), None)
        if not session:
            return flask.jsonify({'error': 'Session not found'}), 404
        return '\n'.join(session['results']), 200, {'Content-Type': 'text/plain'}

    def api_stop(self, sid):
        session = next((s for s in self.sessions if s['sid'] == sid), None)
        if not session:
            return flask.jsonify({'error': 'Session not found'}), 404
        if session['status'] in ['completed', 'stopped']:
            return flask.jsonify({'error': 'Scan not running'}), 400
        session['stop_event'].set()
        session['status'] = 'stopped'
        return flask.jsonify({'status': 'stopped'}), 200

    def run(self, host='0.0.0.0', port=5000):
        self.app.run(host=host, port=port)
