import jsonschema

def nlg_app(base_url="/"):
    app = Flask(__name__)
    nlg_request_format_spec = {
        "type": "object",
        "properties": {
            "template": {"type": "string"},
            "parameters": {"type": "object"}
        },
        "required": ["template"]
    }
    @app.route(base_url, methods=["POST"])
    def handle_nlg():
        data = request.get_json()
        if not data:
            return jsonify({"error": "No JSON payload"}), 400
        try:
            jsonschema.validate(instance=data, schema=nlg_request_format_spec)
        except jsonschema.exceptions.ValidationError as e:
            return jsonify({"error": str(e)}), 400
        template = data.get("template")
        if template == "utter_greet":
            return jsonify({"response": "Hey there!"})
        else:
            return jsonify({"response": "Default message"})
    return app

