import http.client

def http_request(method, action, param=None, **params):
    params_dict = {}
    if param:
        params_dict.update(param)
    params_dict.update(params)

    parsed_action = urllib.parse.urlparse(action)
    original_scheme = parsed_action.scheme
    original_host = parsed_action.hostname
    original_port = parsed_action.port or (443 if original_scheme == 'https' else 80)

    proxy = params.get('proxy')
    if proxy:
        parsed_proxy = urllib.parse.urlparse(proxy)
        proxy_scheme = parsed_proxy.scheme
        proxy_host = parsed_proxy.hostname
        proxy_port = parsed_proxy.port or 80
        if proxy_scheme == 'https':
            conn = http.client.HTTPSConnection(proxy_host, proxy_port)
        else:
            conn = http.client.HTTPConnection(proxy_host, proxy_port)
        headers = {'Host': original_host}
    else:
        if original_scheme == 'https':
            conn = http.client.HTTPSConnection(original_host, original_port)
        else:
            conn = http.client.HTTPConnection(original_host, original_port)
        headers = {}

    if method == 'GET':
        existing_query = parsed_action.query
        encoded_params = urllib.parse.urlencode(params_dict)
        if existing_query:
            new_query = f"{existing_query}&{encoded_params}"
        else:
            new_query = encoded_params
        new_url = parsed_action._replace(query=new_query).geturl()
        action = new_url
    elif method == 'POST':
        body = urllib.parse.urlencode(params_dict)
        headers['Content-Type'] = 'application/x-www-form-urlencoded'
    else:
        body = None

    try:
        conn.request(method, action, body, headers)
        response = conn.getresponse()
        content = response.read().decode('utf-8')
        if 200 <= response.status < 300:
            return content
        else:
            raise Exception(f"HTTP error {response.status}: {response.reason}")
    except Exception as e:
        raise e
    finally:
        conn.close()
